import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-hotel',
  templateUrl: './hotel.component.html',
  styleUrls: ['./hotel.component.css']
})
export class HotelComponent {
HotelForm:FormGroup;
image:any=File;
constructor(private formBuilder: FormBuilder){
  this.HotelForm = new FormGroup({
    registrationId: new FormControl('', [Validators.required]),
    hotelName: new FormControl('',[Validators.required]),
    city: new FormControl('', [Validators.required]),
    rating: new FormControl('',[Validators.required]),
    menus: formBuilder.array([
      formBuilder.group({
        itemId: new FormControl('', [Validators.required]),
        itemName: new FormControl('', [Validators.required]),
        price: new FormControl('', [Validators.required]),
        ratings: new FormControl('', [Validators.required])
      })
    ]),
    rooms: formBuilder.array([
      formBuilder.group({
        roomId: new FormControl('', [Validators.required]),
        roomCatagory: new FormControl('', [Validators.required]),
        roomType: new FormControl('', [Validators.required]),
        price: new FormControl('', [Validators.required]),
        aminitiesList: formBuilder.array([
          formBuilder.group({
            aminity: new FormControl('', [Validators.required])
          })
        ])
      })
    ])
  })
}
onSelectFile(event){
  const imageFile = event.target.files[0];
  this.image = imageFile;
  console.log("image",)
}
uploadData(){
  var formData = new FormData;
  formData.append("file", JSON.stringify(this.image));
  formData.append("data", this.HotelForm.value);
}
}
<head>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatics.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@500&display=swap" rel="stylesheet">
</head>
<mat-toolbar color="primary">
    <h1>Make My Yatraa</h1>
</mat-toolbar>
<div class="a">
    <section style="text-align: center;">

    </section>
<form [formGroup]="HotelForm" (submit)="uploadData()" class="hotel-form">
        <div><span>ADD NEW HOTEL</span></div><br>
        <div class="example-container">
            <mat-form-field appearance="outline">
                <mat-label>Enter Hotel Registration Number</mat-label>
                <input type="text" matInput placeholder="Ex. 242345345543" formControlName="registrationId" required>
            </mat-form-field><br><br>
            <mat-form-field appearance="outline">
                <mat-label>Enter Hotel Name</mat-label>
                <input type="text" matInput placeholder="Ex. Hotel Priyanka Palace" formControlName="hotelName" required>
            </mat-form-field><br><br>
            <mat-form-field appearance="outline">
                <mat-label>Enter City</mat-label>
                <input type="text" matInput placeholder="Ex. Mumbai" formControlName="city" required>
            </mat-form-field><br><br>
            <mat-form-field appearance="outline">
                <mat-label>Enter Rating</mat-label>
        <input type="number" matInput placeholder="Ex. 3.5" formControlName="rating" required>
    </mat-form-field><br><br>
    <div class="file-upload">
        <div class="file-select">
        <div class="file-select-button" id="fileName">Choose File</div>
        <div class="file-select-name" id="noFile">No File Chosen...</div>
<input type="file" name="chooseFile" (change)="onSelectFile($event)" id="choosefile" required>
        </div>
    </div>
<br><br>
    <span>
    <button mat-raised-button color="Warn" type="submit">Register</button></span>
        </div>
</form>
</div>
