import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { LoginServiceService } from '../../Services/login-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm:FormGroup;
  constructor(private login:LoginServiceService){
    this.loginForm = new FormGroup({
      userId: new FormControl('',[Validators.required]),
      password: new FormControl('',[Validators.required]),
      rememberMe: new FormControl('',[])
    })
  }

  loginFun(){
   const data=this.loginForm.value;
   const pass = this.loginForm.get('password')?.value;
    console.log("Password",pass);
   var formData = new FormData();
   formData.append("password", JSON.stringify(pass));
   if(data.userId!='' && data.password!=''){
    this.login.login(data.userId,formData).subscribe((x:any)=>{
      alert("Logged in Successfully...!!!");
      window.location.href="/hotel";
    },error=>{
      alert("Incorrect UserId Or Password...!!!")
    })  
   }
  }
}

<head>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatics.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@500&display=swap" rel="stylesheet">
</head>
<mat-toolbar color="primary">
    <h1>Make My Yatraa</h1>
</mat-toolbar>
<div class="a">
    <section style="text-align: center;">

    </section>
<form [formGroup]="loginForm" (submit)="loginFun()" class="login-form">
        <div><span>LOGIN</span></div><br>
        <p class="example-container">
            <mat-form-field appearance="outline">
                <mat-label>ENTER USER ID</mat-label>
                <input type="text" matInput placeholder="Ex. HP005677BT" formControlName="userId" required>
            </mat-form-field><br><br>
    <mat-form-field appearance="outline">
        <mat-label>Enter Password</mat-label>
<input type="password" matInput placeholder="Ex. Abc@1234" formControlName="password" required>
</mat-form-field><br><br>
<!-- <div>
    <label class="form-check-label" for="rememberMe">
        <input class="form-check-input" type="checkbox" name="rememberMe" id="rememberMe" formControlName="rememberMe" />
        <span>Remember me</span>
      </label>
    </div> -->
    <mat-form-field>
        <label class="form-check-label" for="rememberMe">
            <input class="form-check-input" type="checkbox" name="re">
        </label>
    </mat-form-field>
    <span>
    <button mat-raised-button color="Warn" type="submit">LOGIN</button></span>
</p>
</form>
</div>
