import { Component, ElementRef, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Observable, map, startWith } from 'rxjs';
import {COMMA, ENTER} from '@angular/cdk/keycodes';
import { MatChipInputEvent } from '@angular/material/chips';
import {MatAutocompleteSelectedEvent, MatAutocompleteModule} from '@angular/material/autocomplete';

@Component({
  selector: 'app-add-room',
  templateUrl: './add-room.component.html',
  styleUrls: ['./add-room.component.css']
})
export class AddRoomComponent {
  RoomForm:FormGroup;

  separatorKeysCodes: number[] = [ENTER, COMMA];
  fruitCtrl = new FormControl('');
  filteredFruits: Observable<string[]>;
  fruits: string[] = ['Lemon'];
  allFruits: string[] = ['Apple', 'Lemon', 'Lime', 'Orange', 'Strawberry'];

  @ViewChild('fruitInput')
  fruitInput!: ElementRef<HTMLInputElement>;

  constructor(private formBuilder:FormBuilder) {
    this.RoomForm = this.formBuilder.group({
      roomCatagory: new FormControl('',[Validators.required]),
      roomType: new FormControl('',[Validators.required]),
      price: new FormControl('',[Validators.required]),
      aminitiesList: this.formBuilder.array([
        this.formBuilder.group({
          aminity: new FormControl('',[])
        })
      ])
    });
    this.filteredFruits = this.fruitCtrl.valueChanges.pipe(
      startWith(null),
      map((fruit: string | null) => (fruit ? this._filter(fruit) : this.allFruits.slice())),
    );
  }
  add(event: MatChipInputEvent): void {
    const value = (event.value || '').trim();

    // Add our fruit
    if (value) {
      this.fruits.push(value);
    }

    // Clear the input value
    event.chipInput!.clear();

    this.fruitCtrl.setValue(null);
  }

  remove(fruit: string): void {
    const index = this.fruits.indexOf(fruit);

    if (index >= 0) {
      this.fruits.splice(index, 1);
    }
  }

  selected(event: MatAutocompleteSelectedEvent): void {
    this.fruits.push(event.option.viewValue);
    this.fruitInput.nativeElement.value = '';
    this.fruitCtrl.setValue(null);
  }

  private _filter(value: string): string[] {
    const filterValue = value.toLowerCase();

    return this.allFruits.filter(fruit => fruit.toLowerCase().includes(filterValue));
  }
  uploadData(){}
}
<head>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatics.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@500&display=swap" rel="stylesheet">
</head>
<mat-toolbar color="primary">
    <h1>Make My Yatraa</h1>
</mat-toolbar>
<div class="a">
    <section style="text-align: center;">

    </section>
<form [formGroup]="RoomForm" (submit)="uploadData()" class="hotel-form">
        <div><span>ADD NEW Room</span></div><br>
        <div class="example-container">
            <mat-form-field appearance="outline">
                <mat-label>Enter Room Catagory</mat-label>
                <input type="text" matInput placeholder="Ex. Super Delux" formControlName="roomCatagory" required>
            </mat-form-field><br><br>
            <mat-form-field appearance="outline">
                <mat-label>Enter Room Type</mat-label>
                <input type="text" matInput placeholder="Ex. AC Room" formControlName="roomType" required>
            </mat-form-field><br><br>
            <mat-form-field appearance="outline">
                <mat-label>Enter Price</mat-label>
                <input type="text" matInput placeholder="Ex. Price" formControlName="price" required>
            </mat-form-field><br><br>
           <div formArrayName="aminitiesList">
            <mat-form-field appearance="outline">
                <mat-label>Enter Aminities Provided</mat-label>
                <input type="text" matInput placeholder="Ex. Swimming pool etc." formControlName="Aminities">
            </mat-form-field>
           </div>
           <mat-form-field class="example-chip-list">
            <mat-label>Favorite Fruits</mat-label>
            <mat-chip-grid #chipGrid aria-label="Fruit selection">
              <mat-chip-row *ngFor="let fruit of fruits" (removed)="remove(fruit)">
                {{fruit}}
                <button matChipRemove [attr.aria-label]="'remove ' + fruit">
                  <mat-icon>cancel</mat-icon>
                </button>
              </mat-chip-row>
            </mat-chip-grid>
            <input placeholder="New Fruit..." #fruitInput [formControl="fruitCtrl"
              [matChipInputFor]="chipGrid" [matAutocomplete]="auto"
              [matChipInputSeparatorKeyCodes]="separatorKeysCodes"
              (matChipInputTokenEnd)="add($event)"/>
            <mat-autocomplete #auto="matAutocomplete" (optionSelected)="selected($event)">
              <mat-option *ngFor="let fruit of filteredFruits | async" [value]="fruit">
                {{fruit}}
              </mat-option>
            </mat-autocomplete>
          </mat-form-field>



