import { Component, OnInit } from '@angular/core';
import { ActivateService } from './activate.service';
import { ActivatedRoute, Router } from '@angular/router';
import { mergeMap } from 'rxjs';

@Component({
  selector: 'app-activate',
  templateUrl: './activate.component.html',
  styleUrls: ['./activate.component.css']
})
export class ActivateComponent implements OnInit{
  constructor(private service: ActivateService, private route: ActivatedRoute) {}
  error = false;
  success = false;
  ngOnInit(): void {
    this.route.queryParams.pipe(mergeMap(params => this.service.getk(params.key))).subscribe({
      next: () => (this.success = true),
      error: () => (this.error = true),
    });
  }
}
<div>
    <div class="d-flex justify-content-center">
      <div class="col-md-8">
        <h1>Activation</h1>

        <div class="alert alert-success" *ngIf="success">
          <span><strong>Your user account has been activated.</strong> Please </span>
          <a class="alert-link" routerLink="/login">sign in</a>.
        </div>

        <div class="alert alert-danger" *ngIf="error">
          <strong>Your user could not be activated.</strong> Please use the registration form to sign up.
        </div>
      </div>
    </div>
  </div>
