import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, map } from 'rxjs';
import { Reg } from '../Domain/General-Domain/Reg';
import { Login } from '../Domain/General-Domain/Login';
import { LocalStorageService, SessionStorageService } from 'ngx-webstorage';
import { Account } from '../Domain/General-Domain/Account';
type JwtToken={
  token:string;
}
@Injectable({
  providedIn: 'root'
})
export class LoginServiceService {
  private accountCache$?: Observable<Account> | null;
  private userIdentity: Account | null = null;
  private authenticationState = new ReplaySubject<Account | null>(1);
  constructor(
    private httpclient:HttpClient,
    private localStorageService: LocalStorageService,
    private sessionStorageService: SessionStorageService
    ) { }
  url="http://localhost:9000/api/v1/login";
  url1="http://localhost:9000/api/v1";
  register(data:Reg):Observable<Reg>{
    return this.httpclient.post<Reg>(`${this.url1}/${"register"}`,data);
  }
  login(data:number,pass:FormData,remember:Login):Observable<void>{
    return this.httpclient.post<any>(`${this.url}/${data}`,pass).
    pipe(map(response => this.authenticateUser(response, remember.rememberMe)));
  }
  private authenticateUser(response: JwtToken, rememberMe: boolean):void {

    const jwt = response.token;
    if(rememberMe) {
      console.log(jwt);
      this.localStorageService.store('authenticationToken', jwt);
      this.sessionStorageService.clear('authenticationToken');
    } else{
      this.sessionStorageService.store('authenticationToken', jwt);
      this.localStorageService.clear('authenticationToken');
    }
  }
  private identity(force?: boolean): Observable<Account | null> {
    if (!this.accountCache$ || force) {
      this.accountCache$ = this.fetch().pipe(
        tap((account: Account) => {
          this.authenticate(account);

          this.navigateToStoredUrl();
        }),
        shareReplay()
      );
    }
    return this.accountCache$.pipe(catchError(() => of(null)));
  }
}
