import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm:FormGroup;
  constructor(){
    this.loginForm = new FormGroup({
      userId: new FormControl('',[Validators.required]),
      password: new FormControl('',[Validators.required])
    })
  }
  data:any;
  loginFun(){
   this.data=this.loginForm.value;
   console.log(this.data);
   if(this.data.userId!='' && this.data.password!=''){

   }else
  }

}

<div class="a">
    <section style="text-align: center;">

    </section>
<form [formGroup]="loginForm" (submit)="loginFun()" class="login-form">
        <div><span>LOGIN</span></div><br>
        <p class="example-container">
            <mat-form-field appearance="outline">
                <mat-label>ENTER USER ID</mat-label>
                <input type="number" matInput placeholder="Ex. abc12@gmail.com" formControlName="userId" required>
            </mat-form-field><br><br>
    <mat-form-field appearance="outline">
        <mat-label>Enter Password</mat-label>
<input matInput placeholder="Ex. Abc@1234" formControlName="password" required>
</mat-form-field><br><br>
    <span>
    <button mat-raised-button color="Warn" type="submit">LOGIN</button></span>
</p>
</form>
</div>
