    <mat-grid-tile *ngFor="let l of hotel; let i=index" class="">
    <mat-card>
    <mat-card-content>
    <!-- <img mat-card-lg-image [src]="data"> -->
    <app-image [image]="l.image"></app-image><br>
    <span>{{l.hotelName}}</span><br>
