    <mat-grid-tile *ngFor="let l of hotel" [cols="4" rowHeight="270px">
    <mat-card>
        <mat-card-title-group>
        <div>
            <mat-card-title>
                <input type="text" matInput placeholder="Registration Id" readonly class="inputs">{{l.registrationId}}
            </mat-card-title>
            <mat-card-subtitle>
                <input type="text" matInput placeholder="Hotel Name" readonly class="inputs">{{l.hotelName}}
            </mat-card-subtitle>
        </div>
    </mat-card-title-group>
    <!-- <img mat-card-lg-image [src]="data"> -->
    <app-image [image]="l.image"></app-image>
    </mat-card>
</mat-grid-tile>
</form>
